import PropTypes from 'prop-types';
import shelves from "./Shelves"
import SearchBook from './SearchBook';

const ListBoooks = (props) => {
    const current = props.books.filter(b => b.shelves === "currentlyReading");
    const addToRead = props.books.filter(b=> b.shelves === "wantsToRead");
    const reading = props.books.filter(b=> b.shelves === "reading");

    return (
        <div className='list-books'>
            <div className="list-book-title">
                <h1>My Reads</h1>
            </div>
            <div className='list-books-content'>
                <div className='shelves'>
                    <shelves name = {"currentlyReading"} showSearchPage= {current} updateState = {props.updateState} />
                    <shelves name = {"wantsToRead"} showSearchPage= {addToRead} updateState = {props.updateState} />
                    <shelves name = {"reading"} showSearchPage= {reading} updateState = {props.updateState} />
                </div>
            </div>
            <SearchBook />
        </div>
    )
}

export default ListBoooks;
