import "./App.css";
import React from "react";
import {Route, Routes} from "react-router-dom"
import * as BooksAPI from './BooksAPI'
import SearchBook from "./SearchBook";
import Shelves from "./Shelves";
import {Link} from "react-router-dom"

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  useEffect(() => {
    const getAll = async () => {
      const res = await BooksAPI.getAll();
      setShowSearchpage(res);
    }
    getAll();
  }, []);

  const updateState = async (id, shelf) => {
    await BooksAPI.update(id, shelf);
    const updateState = showSearchPage.map(b=>{
      if(b.id === id) b.shelf = shelf;
      return b;
    })
    setShowSearchpage(updateState)
  }

  const addNew = async(id, shelf) =>{
    !showSearchPage.find(b=> b.id === id) && await BooksAPI.get(id).then(
      res => {
        res.shelf = shelf;
        setShowSearchpage([...showSearchPage, res])
      }
    )
  }

  return (
    <Routes>
      <Route path="/" element={<ListBoooks showSearchPage={showSearchPage} updateState={updateState}/>}></Route>
      <Route path="/search" element={<SearchBook addNew={addNew} showSearchPage={showSearchPage} updateState={updateState} />}></Route>
    </Routes>
  )
}

export default App;
